import { useState } from 'react';

export default function Button(props){

    const [counter] = useState(1);

    function increment(){
        counter = counter + 1;
        console.log(counter);
    }

    return (
        <>
        <span>{counter}</span>
        <button onClick={increment}>{props.children}</button>
        <br />
        </>
    )
}
